pub enum Error {
    MissingPolynomial {
        label: String,
    },
    MissingEvaluation {
        label: String,
    },
    MissingLHS {
        label: String,
    },
    MissingRng,
    DegreeIsZero,
    TooManyCoefficients {
        num_coefficients: usize,
        num_powers: usize,
    },
    HidingBoundIsZero,
    HidingBoundToolarge {
        hiding_poly_degree: usize,
        num_powers: usize,
    },
    TrimmingDegreeTooLarge,
    EmptyDegreeBounds,
    EquationHasDegreeBounds(String),
    UnsupportedDegreeBound(usize),
    IncorrectDegreeBound {
        poly_degree: usize,
        degree_bound: usize,
        supported_degree: usize,
        label: String,
    },
    IncorrectInputLength(String),
    InvalidNumberOfVariables,
    PolynomialDegreeTooLarge {
        poly_degree: usize,
        supported_degree: usize,
        label: String,
    },
}
